local e=gui.Tab(gui.Reference("Misc"),"autoreport","Auto Report")local t=gui.Groupbox(e,"Options",4,4,200,400)local a do local d=gui.Checkbox(t,"enable","Enable Autoreport",false)d:SetDescription("Automatically report the player who kills you.")a=d end local o=gui.Multibox(t,"Reasons")local i=gui.Checkbox(o,"reasons.abusive_communication","Abusive Communication",true)local n=gui.Checkbox(o,"reasons.griefing","Griefing",true)local s=gui.Checkbox(o,"reasons.aim","Aimhacking",true)local h=gui.Checkbox(o,"reasons.wall","Wallhacking",true)local r=gui.Checkbox(o,"reasons.external","External assistance",true)client.AllowListener("player_death")callbacks.Register("FireGameEvent",function(d)if d:GetName()~="player_death"then return end if client.GetPlayerIndexByUserID(d:GetInt("userid"))~=client.GetLocalPlayerIndex()then return end if not a:GetValue()then return end local l={}if i:GetValue()then table.insert(l,"textabuse")end if n:GetValue()then table.insert(l,"grief")end if s:GetValue()then table.insert(l,"aimbot")end if h:GetValue()then table.insert(l,"wallhack")end if r:GetValue()then table.insert(l,"speedhack")end local u=d:GetInt("attacker")local c="7656".. (0x116ebff0000+u)print("reporting",c,"for",table.concat(l,','))return panorama.RunScript("GameStateAPI.SubmitPlayerReport( '"..tostring(c).."', '"..tostring(table.concat(l,',')).."' )")end)